package org.eclipse.dataspaceconnector.iam.ion.util;

import org.assertj.core.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 *
 */
class StringUtilsTest {
    @Test
    void encodeToHexBytes() {

        final var input = "cd18bb83b12080fae7945d31688f62ad968a7e980feb22642729d104e3097fe2";
        final var expectedOutput = new byte[]{
                (byte) 205,
                24,
                (byte) 187,
                (byte) 131,
                (byte) 177,
                32,
                (byte) 128,
                (byte) 250,
                (byte) 231,
                (byte) 148,
                93,
                49,
                104,
                (byte) 143,
                98,
                (byte) 173,
                (byte) 150,
                (byte) 138,
                (byte) 126,
                (byte) 152,
                15,
                (byte) 235,
                34,
                100,
                39,
                41,
                (byte) 209,
                4,
                (byte) 227,
                9,
                127,
                (byte) 226

        };

        var encoded = StringUtils.encodeToHexBytes(input);
        Assertions.assertThat(encoded).isEqualTo(expectedOutput);

    }

    @Test
    void encodeToHex() {
        var input = "";
        var expectedOutput = "";

        Assertions.assertThat(StringUtils.encodeToHex(input)).isEqualTo(expectedOutput);
    }
}
